<script setup>
import VChart from 'vue-echarts'
import * as echarts from 'echarts'

const props = defineProps({
  source: {
    type: Array,
    default: () => {
      return [
        { name: '饲料1', value: 43.3, max_value: 100 },
        { name: '饲料2', value: 83.1, max_value: 100 },
        { name: '饲料3', value: 86.4, max_value: 100 },
        { name: '饲料4', value: 72.4, max_value: 100 },
      ]
    },
  },
})

const option = ref({
  grid: {
    top: 10,
    bottom: 10,
    left: 10,
    right: 10,
  },
  backgroundColor: 'transparent',
  xAxis: { type: 'value', show: false },
  yAxis: [
    {
      type: 'category',
      inverse: true,
      show: false,
    },
    {
      type: 'category',
      inverse: true,
      axisTick: 'none',
      axisLine: 'none',
      position: 'left',
      // offset: -10,
      data: props.source.map(item => item.name),
      axisLabel: {
        interval: 0,
        align: 'left',
        verticalAlign: 'bottom',
        lineHeight: 42,
        padding: [8, 10],
        formatter(value) {
          return `{a|}{b|${value}}`
        },
        rich: {
          a: {
            backgroundColor: { image: 'data:image/png;base64,' },
            height: 20,
            width: 20,
          },
          b: {
            padding: [6, 12],
            color: '#fff',
            fontSize: 16,
          },
        },
      },
    },
    {
      type: 'category',
      inverse: true,
      axisTick: 'none',
      axisLine: 'none',
      data: props.source.map((item) => {
        return `${(item.value / item.max_value * 100)}%`
      }),
      axisLabel: {
        align: 'right',
        formatter: '{value}',
        lineHeight: 42,
        color: '#fff',
        fontSize: 16,
        verticalAlign: 'bottom',
        padding: [8, 10],
      },
    },
  ],
  dataset: {
    dimensions: ['name', 'value', 'max_value'],
    source: props.source,
  },
  series: [
    {
      name: 'value',
      type: 'bar',
      yAxisIndex: 0,
      barWidth: 12,
      itemStyle: {
        borderRadius: 12,
        color: new echarts.graphic.LinearGradient(0, 0, 1, 0, [
          {
            offset: 0,
            color: '#1df0e4',
          },
          {
            offset: 1,
            color: '#128fe4',
          },
        ]),
      },
    },
    {
      name: 'max_value',
      type: 'bar',
      barWidth: 12,
      barGap: '-100%',
      // yAxisIndex: 2, 指定第二条Y轴数据
      // barCategoryGap: 5, 设置宽度后间隙失效
      z: 0,
      itemStyle: {
        color: 'rgba(5, 43, 80,.5)',
        borderRadius: 12,
      },
    },
  ],
})
</script>

<template>
  <VChart class="chart" :option="option" autoresize />
</template>

<style scoped>
.chart {
  width: 100%;
  height: 100%;
}
</style>
